# The extern keyword declares a function defined elsewhere (in C standard library)
extern printf(fmt: string, ...);

# Every BPL program needs a main frame
# 'frame' is BPL's keyword for functions
# 'ret int' specifies the return type
frame main() ret int {
    # Call printf to display a message
    printf("Hello, World!\n");

    # Return 0 to indicate success
    return 0;
}
