extern printf(fmt: string, ...);

frame main() ret int {
    local a: int;
    local b: int;

    a = 10;
    b = 3;

    # Basic arithmetic operations
    printf("%d + %d = %d\n", a, b, a + b);
    printf("%d - %d = %d\n", a, b, a - b);
    printf("%d * %d = %d\n", a, b, a * b);
    printf("%d / %d = %d\n", a, b, a / b);
    printf("%d %% %d = %d\n", a, b, a % b);

    # Float arithmetic
    local x: float;
    local y: float;
    x = 10.5;
    y = 3.2;

    printf("%.2f + %.2f = %.2f\n", x, y, x + y);
    printf("%.2f * %.2f = %.2f\n", x, y, x * y);

    return 0;
}
