extern printf(fmt: string, ...);

frame main() ret int {
    local age: int;
    age = 18;

    # Simple if statement
    if (age >= 18) {
        printf("You are an adult\n");
    }

    # If-else statement
    local score: int;
    score = 85;

    if (score >= 90) {
        printf("Grade: A\n");
    } else if (score >= 80) {
        printf("Grade: B\n");
    } else if (score >= 70) {
        printf("Grade: C\n");
    } else {
        printf("Grade: F\n");
    }

    # Comparison operators
    local x: int;
    local y: int;
    x = 5;
    y = 10;

    if (x == y) { printf("Equal\n"); }
    if (x != y) { printf("Not equal\n"); }
    if (x < y) { printf("%d < %d\n", x, y); }

    return 0;
}
