extern printf(fmt: string, ...);

frame main() ret int {
    # Basic while loop
    local count: int;
    count = 0;

    printf("Counting to 5:\n");
    loop (count < 5) {
        count = count + 1;
        printf("%d ", count);
    }
    printf("\n");

    # While with break
    local i: int;
    i = 0;
    printf("\nWith break at 3:\n");
    loop (i < 10) {
        i = i + 1;
        if (i == 3) {
            break;
        }
        printf("%d ", i);
    }
    printf("\n");

    # While with continue
    local j: int;
    j = 0;
    printf("\nSkip even numbers:\n");
    loop (j < 10) {
        j = j + 1;
        if (j % 2 == 0) {
            continue;
        }
        printf("%d ", j);
    }
    printf("\n");

    return 0;
}
