extern printf(fmt: string, ...);

frame main() ret int {
    # Basic for loop
    local i: int;
    printf("Count 0 to 9:\n");
    i = 0;
    loop (i < 10) {
        printf("%d ", i);
        i = i + 1;
    }
    printf("\n");

    # For loop with step of 2
    local j: int;
    printf("\nEven numbers 0 to 10:\n");
    j = 0;
    loop (j <= 10) {
        printf("%d ", j);
        j = j + 2;
    }
    printf("\n");

    # Nested for loops
    local row: int;
    local col: int;
    printf("\nMultiplication table:\n");
    row = 1;
    loop (row <= 5) {
        col = 1;
        loop (col <= 5) {
            printf("%3d ", row * col);
            col = col + 1;
        }
        printf("\n");
        row = row + 1;
    }

    return 0;
}
