extern printf(fmt: string, ...);

# Frame with parameters and return value
frame add(a: int, b: int) ret int {
    return a + b;
}

# Frame with no return value (void)
frame greet(name: string) {
    printf("Hello, %s!\n", name);
}

# Frame that returns a boolean
frame isEven(n: int) ret bool {
    return n % 2 == 0;
}

# Frame with multiple statements
frame factorial(n: int) ret int {
    if (n <= 1) {
        return 1;
    }
    return n * factorial(n - 1);
}

frame main() ret int {
    # Call frames
    local sum: int;
    sum = add(5, 3);
    printf("5 + 3 = %d\n", sum);

    greet("Alice");

    if (isEven(4)) {
        printf("4 is even\n");
    }

    printf("5! = %d\n", factorial(5));

    return 0;
}
