extern printf(fmt: string, ...);

# Define a struct with multiple fields
struct Person {
    name: string,
    age: int,
    height: float,
}

struct Point {
    x: int,
    y: int,
}

frame printPerson(p: Person) {
    printf("Person: %s, age %d, height %.1f\n", 
           p.name, p.age, p.height);
}

frame distance(p1: Point, p2: Point) ret int {
    local dx: int;
    local dy: int;
    dx = p2.x - p1.x;
    dy = p2.y - p1.y;
    # Simplified distance (Manhattan distance)
    if (dx < 0) { dx = -dx; }
    if (dy < 0) { dy = -dy; }
    return dx + dy;
}

frame main() ret int {
    # Create and use a Person
    local alice: Person;
    alice.name = "Alice";
    alice.age = 30;
    alice.height = 5.6;

    printPerson(alice);

    # Create and use Points
    local p1: Point;
    local p2: Point;
    p1.x = 0;
    p1.y = 0;
    p2.x = 3;
    p2.y = 4;

    printf("Distance: %d\n", distance(p1, p2));

    return 0;
}
