extern printf(fmt: string, ...);

frame swap(a: *int, b: *int) {
    local temp: int;
    temp = *a;
    *a = *b;
    *b = temp;
}

frame main() ret int {
    local x: int;
    local y: int;
    local ptr: *int;

    x = 10;
    y = 20;

    printf("Before: x=%d, y=%d\n", x, y);

    # Get address of x
    ptr = &x;
    printf("x is at address %p\n", ptr);
    printf("Value at address: %d\n", *ptr);

    # Modify through pointer
    *ptr = 15;
    printf("After modification: x=%d\n", x);

    # Swap using pointers
    swap(&x, &y);
    printf("After swap: x=%d, y=%d\n", x, y);

    return 0;
}
