extern printf(fmt: string, ...);

frame main() ret int {
    # Declare an array of 5 integers
    local numbers: int[5];
    local i: int;

    # Initialize array elements
    numbers[0] = 10;
    numbers[1] = 20;
    numbers[2] = 30;
    numbers[3] = 40;
    numbers[4] = 50;

    # Print array elements
    printf("Array elements:\n");
    i = 0;
    loop (i < 5) {
        printf("numbers[%d] = %d\n", i, numbers[i]);
        i = i + 1;
    }

    # Calculate sum
    local sum: int;
    sum = 0;
    i = 0;
    loop (i < 5) {
        sum = sum + numbers[i];
        i = i + 1;
    }
    printf("Sum: %d\n", sum);

    # Find maximum
    local max: int;
    max = numbers[0];
    i = 1;
    loop (i < 5) {
        if (numbers[i] > max) {
            max = numbers[i];
        }
        i = i + 1;
    }
    printf("Maximum: %d\n", max);

    return 0;
}
