extern printf(fmt: string, ...);
extern strlen(s: string) ret int;

frame greetUser(name: string, title: string) {
    printf("%s %s, welcome!\n", title, name);
}

frame main() ret int {
    local message: string;
    local name: string;

    message = "Hello from BPL!";
    name = "Alice";

    # Print strings
    printf("%s\n", message);
    printf("Name: %s\n", name);

    # String with function
    greetUser("Smith", "Dr.");
    greetUser("Johnson", "Prof.");

    # String length (if strlen is available)
    local len: int;
    len = strlen(message);
    printf("Message length: %d\n", len);

    # Escape sequences
    printf("Tab:\there\n");
    printf("Quote: \"Hello\"\n");
    printf("Newline below:\n\n");

    return 0;
}
