extern printf(fmt: string, ...);

frame main() ret int {
    local age: int;
    local hasLicense: bool;
    local hasInsurance: bool;

    age = 20;
    hasLicense = true;
    hasInsurance = true;

    # AND operator (&&)
    if (age >= 18 && hasLicense) {
        printf("You can drive!\n");
    }

    # OR operator (||)
    if (age < 18 || !hasLicense) {
        printf("Cannot drive alone\n");
    } else {
        printf("Can drive alone\n");
    }

    # NOT operator (!)
    if (!hasInsurance) {
        printf("Get insurance first!\n");
    }

    # Complex conditions
    local score: int;
    local attendance: int;
    score = 85;
    attendance = 95;

    if ((score >= 80 && attendance >= 90) || score >= 95) {
        printf("Excellent performance!\n");
    }

    # Boolean variables
    local isWeekend: bool;
    local isHoliday: bool;
    isWeekend = false;
    isHoliday = true;

    if (isWeekend || isHoliday) {
        printf("Day off!\n");
    } else {
        printf("Work day\n");
    }

    return 0;
}
