extern printf(fmt: string, ...);

frame max(a: int, b: int) ret int {
    return a > b ? a : b;
}

frame min(a: int, b: int) ret int {
    return a < b ? a : b;
}

frame abs(n: int) ret int {
    return n < 0 ? -n : n;
}

frame main() ret int {
    local a: int;
    local b: int;

    a = 10;
    b = 20;

    # Simple ternary
    printf("max(%d, %d) = %d\n", a, b, max(a, b));
    printf("min(%d, %d) = %d\n", a, b, min(a, b));

    # Absolute value
    local n: int;
    n = -15;
    printf("abs(%d) = %d\n", n, abs(n));

    # Check even/odd
    local num: int;
    num = 7;
    printf("%d is %s\n", num, num % 2 == 0 ? "even" : "odd");

    # Grade assignment
    local score: int;
    local grade: string;
    score = 85;
    grade = score >= 90 ? "A" : score >= 80 ? "B" : score >= 70 ? "C" : "F";
    printf("Score %d = Grade %s\n", score, grade);

    # Sign of number
    local value: int;
    local sign: string;
    value = -5;
    sign = value > 0 ? "positive" : value < 0 ? "negative" : "zero";
    printf("%d is %s\n", value, sign);

    return 0;
}
