extern printf(fmt: string, ...);

frame main() ret int {
    local a: int;
    local b: int;

    a = 12;  # 1100 in binary
    b = 10;  # 1010 in binary

    printf("a = %d (binary: 1100)\n", a);
    printf("b = %d (binary: 1010)\n", b);
    printf("\n");

    # Bitwise AND
    printf("a & b = %d\n", a & b);

    # Bitwise OR
    printf("a | b = %d\n", a | b);

    # Bitwise XOR
    printf("a ^ b = %d\n", a ^ b);

    # Left shift
    printf("a << 1 = %d\n", a << 1);

    # Right shift
    printf("a >> 1 = %d\n", a >> 1);

    return 0;
}
