extern printf(fmt: string, ...);

# Global variables
global counter: int;
global PI: float;
global appName: string;

frame increment() {
    counter = counter + 1;
}

frame getCounter() ret int {
    return counter;
}

frame circleArea(radius: float) ret float {
    return PI * radius * radius;
}

frame printHeader() {
    printf("=== %s ===\n", appName);
}

frame main() ret int {
    # Initialize globals
    counter = 0;
    PI = 3.14159;
    appName = "BPL Demo";

    printHeader();

    # Use global counter
    printf("Initial counter: %d\n", getCounter());
    increment();
    increment();
    increment();
    printf("After increments: %d\n", getCounter());

    # Use global PI
    local radius: float;
    radius = 5.0;
    printf("\nCircle with radius %.1f:\n", radius);
    printf("  Area: %.2f\n", circleArea(radius));

    # Modify global directly
    counter = 100;
    printf("\nCounter reset to: %d\n", counter);

    return 0;
}
