extern printf(fmt: string, ...);

frame fibonacci(n: int) ret int {
    if (n <= 1) {
        return n;
    }
    return fibonacci(n - 1) + fibonacci(n - 2);
}

frame main() ret int {
    local i: int;

    printf("First 8 Fibonacci numbers:\n");
    i = 0;
    loop (i < 8) {
        printf("%d ", fibonacci(i));
        i = i + 1;
    }
    printf("\n");

    return 0;
}
