# Import from the standard library (using stdlib modules)
# BPL provides built-in utilities in the std library

# import [IO] from "std/io.bpl";
# Import currently not supported in playground

extern printf(fmt: string, ...);
extern strlen(str: string) ret int;

frame main() ret int {
    # Example: working with string functions
    local message: string = "Hello, BPL!";

    # Get string length
    local length: int = strlen(message);
    printf("Message: %s\n", message);
    printf("Length: %d\n", length);

    # Work with arrays
    local numbers: int[5] = [1, 2, 3, 4, 5];
    local i: int = 0;
    printf("Array contents: ");
    loop (i < 5) {
        # IO.printInt(numbers[i]);
        printf("%d ", numbers[i]);
        i = i + 1;
    }
    printf("\n");

    return 0;
}
