# In a real project, you would have multiple .bpl files:
# - utils.bpl (containing utility functions)
# - types.bpl (containing type definitions)
# - main.bpl (this file)
# Use: import [Point] from "./types.bpl";

# For this example, we define the types locally
struct Point {
    x: int,
    y: int,
}

struct Config {
    version: int,
    enabled: bool,
}

# Utility functions (would be in utils.bpl)
frame add(a: int, b: int) ret int {
    return a + b;
}

frame multiply(a: int, b: int) ret int {
    return a * b;
}

extern printf(fmt: string, ...);

frame main() ret int {
    # Use utility functions
    local result1: int = add(10, 20);
    local result2: int = multiply(5, 6);
    printf("10 + 20 = %d\n", result1);
    printf("5 * 6 = %d\n", result2);

    # Use types
    local point: Point;
    point.x = 100;
    point.y = 200;
    printf("Point at (%d, %d)\n", point.x, point.y);

    # Use Config type
    local config: Config;
    config.version = 1;
    config.enabled = true;
    printf("Config v%d, Enabled: %d\n", config.version, config.enabled);

    return 0;
}
