# Define constants for color values (enum-like pattern)
global Color_RED: int = 1;
global Color_GREEN: int = 2;
global Color_BLUE: int = 3;
global Color_YELLOW: int = 4;

# Define constants for directions
global Direction_UP: int = 0;
global Direction_DOWN: int = 1;
global Direction_LEFT: int = 2;
global Direction_RIGHT: int = 3;

extern printf(fmt: string, ...);

frame getColorName(color: int) ret string {
    if (color == 1) {
        return "RED";
    } else if (color == 2) {
        return "GREEN";
    } else if (color == 3) {
        return "BLUE";
    } else if (color == 4) {
        return "YELLOW";
    } else {
        return "UNKNOWN";
    }
}

frame main() ret int {
    # Use constant values (enum-like pattern)
    local myColor: int = Color_RED;
    local myDirection: int = Direction_UP;

    printf("Selected color code: %d\n", myColor);
    printf("Selected direction code: %d\n", myDirection);

    # Get color name
    local colorName: string = getColorName(Color_BLUE);
    printf("Color name: %s\n", colorName);

    # Constants in switch statement
    local direction: int = Direction_LEFT;
    switch (direction) {
        case 0: { printf("Moving UP\n"); }
        case 1: { printf("Moving DOWN\n"); }
        case 2: { printf("Moving LEFT\n"); }
        case 3: { printf("Moving RIGHT\n"); }
        default: { printf("Unknown direction\n"); }
    }

    return 0;
}
