# Simple type aliases
type Age = int;
type Score = float;
type Name = string;

extern printf(fmt: string, ...);

frame main() ret int {
    # Use type aliases
    local myAge: Age = 25;
    local myScore: Score = 95.5;
    local myName: Name = "Alice";

    printf("Name: %s, Age: %d, Score: %.1f\n", myName, myAge, myScore);

    # Work with simple types
    local x: int = 10;
    local y: int = 20;
    printf("Values: x=%d, y=%d\n", x, y);

    # Create an array
    local numbers: int[5] = [1, 2, 3, 4, 5];
    local i: int = 0;
    printf("Array: ");
    loop (i < 5) {
        printf("%d ", numbers[i]);
        i = i + 1;
    }
    printf("\n");

    return 0;
}
